import { useState, useEffect } from 'react';
import useAuth from './useAuth';
import Player from './Player';
import TrackSearchResult from './TrackSearchResult';
import Playlist from './Playlist'; 
import { Container, Form, Button } from 'react-bootstrap';
import SpotifyWebApi from 'spotify-web-api-node';
import './Dashboard.css'; // Custom CSS for additional styling

const spotifyApi = new SpotifyWebApi({
  clientId: "f9bfead203b948469f6b139d3ff345a4",
});

export default function Dashboard({ code }) {
  const accessToken = useAuth(code);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [playingTrack, setPlayingTrack] = useState();
  const [playlists, setPlaylists] = useState([]);
  const [selectedPlaylist, setSelectedPlaylist] = useState(null);
  const [playlistTracks, setPlaylistTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.setAccessToken(accessToken);

    spotifyApi.getUserPlaylists().then((response) => {
      setPlaylists(response.body.items);
    });
  }, [accessToken]);

  useEffect(() => {
    if (selectedPlaylist) {
      spotifyApi.getPlaylistTracks(selectedPlaylist.id).then(response => {
        setPlaylistTracks(response.body.items.map(item => item.track));
        setCurrentTrackIndex(0); 
      });
    }
  }, [selectedPlaylist]);

  useEffect(() => {
    if (currentTrackIndex >= 0 && playlistTracks.length > 0) {
      setPlayingTrack(playlistTracks[currentTrackIndex]);
    }
  }, [currentTrackIndex, playlistTracks]);

  function chooseTrack(track) {
    setPlayingTrack(track);
    setCurrentTrackIndex(playlistTracks.findIndex(t => t.uri === track.uri));
    setSearch("");
  }

  function playNextTrack() {
    if (currentTrackIndex < playlistTracks.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
    }
  }

  function playPreviousTrack() {
    if (currentTrackIndex > 0) {
      setCurrentTrackIndex(currentTrackIndex - 1);
    }
  }

  useEffect(() => {
    if (!search) return setSearchResults([]);
    if (!accessToken) return;

    let cancel = false;
    spotifyApi.searchTracks(search).then(res => {
      if (cancel) return;
      setSearchResults(
        res.body.tracks.items.map(track => {
          const smallestAlbumImage = track.album.images.reduce(
            (smallest, image) => {
              if (image.height < smallest.height) return image;
              return smallest;
            },
            track.album.images[0]
          );

          return {
            artist: track.artists[0].name,
            title: track.name,
            uri: track.uri,
            albumUrl: smallestAlbumImage.url,
          };
        })
      );
    });

    return () => (cancel = true);
  }, [search, accessToken]);

  return (
    <Container className="d-flex flex-column py-4 full-height">
      <Form.Control
        type="search"
        placeholder="Search Songs/Artists"
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="mb-3 search-bar"
      />
      <div className="d-flex flex-grow-1">
        <div className="playlist-sidebar">
          <h5 className="text-light">Your Playlists</h5>
          {playlists.map(playlist => (
            <Playlist
              key={playlist.id}
              playlist={playlist}
              setSelectedPlaylist={setSelectedPlaylist}
            />
          ))}
        </div>
        <div className="flex-grow-1 my-2 track-list">
          {selectedPlaylist && (
            <div>
              <h5 className="text-light">Tracks in {selectedPlaylist.name}</h5>
              {playlistTracks.map(track => (
                <TrackSearchResult
                  key={track.uri}
                  track={{
                    artist: track.artists[0].name,
                    title: track.name,
                    uri: track.uri,
                    albumUrl: track.album.images[0]?.url,
                  }}
                  chooseTrack={chooseTrack}
                />
              ))}
            </div>
          )}
          {searchResults.map(track => (
            <TrackSearchResult
              track={track}
              key={track.uri}
              chooseTrack={chooseTrack}
            />
          ))}
        </div>
      </div>
      <div className="d-flex justify-content-center align-items-center mt-4 player-controls">
        <Button 
          onClick={playPreviousTrack} 
          disabled={currentTrackIndex === 0} 
          className="control-button"
        >
          Previous
        </Button>
        <Player accessToken={accessToken} trackUri={playingTrack?.uri} />
        <Button 
          onClick={playNextTrack} 
          disabled={currentTrackIndex === playlistTracks.length - 1} 
          className="control-button"
        >
          Next
        </Button>
      </div>
    </Container>
  );
}
