import { useState, useEffect, useCallback } from "react";
import SpotifyPlayer from "react-spotify-web-playback";

export default function Player({ accessToken, trackUri, onNext, onPrevious }) {
  const [play, setPlay] = useState(false);

  useEffect(() => setPlay(true), [trackUri]);

  const handlePlay = useCallback(() => {
    setPlay(true);
  }, []);

  if (!accessToken) return null;

  return (
    <div className="player-container">
      <SpotifyPlayer
        token={accessToken}
        showSaveIcon
        callback={state => {
          if (!state.isPlaying) setPlay(false);
        }}
        play={play}
        uris={trackUri ? [trackUri] : []}
      />
      <div className="player-controls">
        <button className="player-control-button" onClick={onPrevious}>
          &#9664;
        </button>
        <button className="player-control-button play-button" onClick={handlePlay}>
          &#9654;
        </button>
        <button className="player-control-button" onClick={onNext}>
          &#9654;
        </button>
      </div>
    </div>
  );
}
