import { useState, useEffect } from 'react';
import useAuth from './useAuth';
import Player from './Player';
import TrackSearchResult from './TrackSearchResult';
import Playlist from './Playlist'; 
import { Container, Form } from 'react-bootstrap';
import SpotifyWebApi from 'spotify-web-api-node';

const spotifyApi = new SpotifyWebApi({
	clientId: "f9bfead203b948469f6b139d3ff345a4",
  });
  
  export default function Dashboard({ code }) {
	const accessToken = useAuth(code);
	const [search, setSearch] = useState("");
	const [searchResults, setSearchResults] = useState([]);
	const [playingTrack, setPlayingTrack] = useState();
	const [playlists, setPlaylists] = useState([]);
	const [selectedPlaylist, setSelectedPlaylist] = useState(null);
	const [playlistTracks, setPlaylistTracks] = useState([]);
  
	useEffect(() => {
	  if (!accessToken) return;
	  spotifyApi.setAccessToken(accessToken);
  
	  spotifyApi.getUserPlaylists().then((response) => {
		setPlaylists(response.body.items);
	  });
	}, [accessToken]);
  
	useEffect(() => {
	  if (selectedPlaylist) {
		spotifyApi.getPlaylistTracks(selectedPlaylist.id).then(response => {
		  setPlaylistTracks(response.body.items.map(item => item.track));
		});
	  }
	}, [selectedPlaylist]);
  
	function chooseTrack(track) {
	  setPlayingTrack(track);
	  setSearch("");
	}
  
	useEffect(() => {
	  if (!search) return setSearchResults([]);
	  if (!accessToken) return;
  
	  let cancel = false;
	  spotifyApi.searchTracks(search).then(res => {
		if (cancel) return;
		setSearchResults(
		  res.body.tracks.items.map(track => {
			const smallestAlbumImage = track.album.images.reduce(
			  (smallest, image) => {
				if (image.height < smallest.height) return image;
				return smallest;
			  },
			  track.album.images[0]
			);
  
			return {
			  artist: track.artists[0].name,
			  title: track.name,
			  uri: track.uri,
			  albumUrl: smallestAlbumImage.url,
			};
		  })
		);
	  });
  
	  return () => (cancel = true);
	}, [search, accessToken]);
  
	return (
	  <Container className="d-flex flex-column py-2" style={{ height: "100vh" }}>
		<Form.Control
		  type="search"
		  placeholder="Search Songs/Artists"
		  value={search}
		  onChange={e => setSearch(e.target.value)}
		/>
		<div className="d-flex">
		  <div className="w-25">
			<h5>Your Playlists</h5>
			{playlists.map(playlist => (
			  <Playlist
				key={playlist.id}
				playlist={playlist}
				setSelectedPlaylist={setSelectedPlaylist}
			  />
			))}
		  </div>
		  <div className="flex-grow-1 my-2" style={{ overflowY: "auto" }}>
			{selectedPlaylist && (
			  <div>
				<h5>Tracks in {selectedPlaylist.name}</h5>
				{playlistTracks.map(track => (
				  <TrackSearchResult
					key={track.uri}
					track={{
					  artist: track.artists[0].name,
					  title: track.name,
					  uri: track.uri,
					  albumUrl: track.album.images[0]?.url,
					}}
					chooseTrack={chooseTrack}
				  />
				))}
			  </div>
			)}
			{searchResults.map(track => (
			  <TrackSearchResult
				track={track}
				key={track.uri}
				chooseTrack={chooseTrack}
			  />
			))}
		  </div>
		</div>
		<div>
		  <Player accessToken={accessToken} trackUri={playingTrack?.uri} />
		</div>
	  </Container>
	);
  }
