import { useState, useEffect } from "react";
import SpotifyPlayer from "react-spotify-web-playback";

export default function Player({ accessToken, trackUri }) {
  const [play, setPlay] = useState(false);
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    if (window.Spotify) {
      const newPlayer = new window.Spotify.Player({
        name: "MusicAI Player",
        getOAuthToken: cb => cb(accessToken),
        volume: 0.5
      });

      newPlayer.addListener("ready", ({ device_id }) => {
        console.log("Player is ready");
        setPlayer(newPlayer);
      });

      newPlayer.addListener("not_ready", () => {
        console.log("Player is not ready");
        setPlayer(null);
      });

      newPlayer.connect();
    }
  }, [accessToken]);

  useEffect(() => {
    if (player) {
      player.togglePlay().then(() => console.log("Toggled playback"));
    }
  }, [trackUri, player]);

  const handleNext = () => {
    if (player) {
      player.nextTrack().catch(err => console.error(err));
    }
  };

  const handlePrevious = () => {
    if (player) {
      player.previousTrack().catch(err => console.error(err));
    }
  };

  if (!accessToken) return null;

  return (
    <div className="player-container">
      <SpotifyPlayer
        token={accessToken}
        showSaveIcon
        callback={state => {
          if (!state.isPlaying) setPlay(false);
        }}
        play={play}
        uris={trackUri ? [trackUri] : []}
      />
      <div className="player-controls">
        <button className="player-control-button" onClick={handlePrevious}>
          &#9664;
        </button>
        <button className="player-control-button play-button" onClick={() => setPlay(!play)}>
          {play ? "Pause" : "Play"}
        </button>
        <button className="player-control-button" onClick={handleNext}>
          &#9654;
        </button>
      </div>
    </div>
  );
}
