import { useState, useEffect } from 'react';
import SpotifyPlayer from 'react-spotify-web-playback';

export default function Player({ accessToken, trackUri }) {
  const [play, setPlay] = useState(false);

  useEffect(() => setPlay(true), [trackUri]);

  const handleSkip = (direction) => {
    if (!accessToken) return;

    // Use the Spotify Web Playback SDK to perform skip actions
    window.spotifyPlayer[direction]();
  };

  if (!accessToken) return null;

  return (
    <div className="player-container">
      <SpotifyPlayer
        token={accessToken}
        showSaveIcon
        callback={(state) => {
          if (!state.isPlaying) setPlay(false);
        }}
        play={play}
        uris={trackUri ? [trackUri] : []}
      />
      <div className="player-controls">
        <button onClick={() => handleSkip('previous')}>&lt;</button>
        <button onClick={() => handleSkip('togglePlay')}>{play ? 'Pause' : 'Play'}</button>
        <button onClick={() => handleSkip('next')}>&gt;</button>
      </div>
    </div>
  );
}
